package adiiapplication;

public class IfElseStatmnt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		if(a+b<40)
		{
			System.out.println("a+b is less than 40");
		}
		else
		{
			System.out.println("a+b is greater than 40");
		}
	}
}

