package adiiapplication;

public class IfelseExmpl {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=12;
		if(a+b<30)
		{
			System.out.println("a+b is less than 30");
		}
		else
		{
			System.out.println("a+b is greater than 30");
		}
		
	}

}
